UPDATE `sgulot` SET `mcudot` = '\r\n<p>- <small class=\"small\">(מוסב על האמור למעלה \'אמת קנה\', לומר:)</small> הלא \"גיל יגיל... יולד חכם\", לזה \"קנה אמת\", ו<strong>ישמח אביך ואמך ותגל יולדתך</strong> - המלמדתך, המולידה החכמה בלבך.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 25 AND 1 LIMIT 1;

