UPDATE `sgulot` SET `mcudot` = '<p>- כל הדברים האלה מיוחדים המה <strong>להמאחרים</strong> (מתעכבים) שֶבֶת <strong>על היין</strong>, ול<strong>הבאים לחקור</strong> אחר יין ש<strong>ממסכו</strong> (מזגו, עירובו במים) יפה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 30 AND 1 LIMIT 1;

