SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '<p>- <strong>והיית כ</strong>אנשי הספינה ההולכת <strong>באמצע הים</strong> - כשהרוח חזק, דרכם להיות <strong>שוכבים</strong> בעבור חוזק תנועת הספינה;&#160;&#160; <strong>וכשוכב בראש חבל</strong> - הוא תורן הספינה, שכל החבלים תלוים בו; והשוכב שם, דעתו מבולבלת מפני ראיית הים ומהירות התנועה -</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 34 AND 1 LIMIT 1;

