UPDATE `sgulot` SET `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק     <strong class=\"selflink\">ה</strong>                             </p>\r\n</div>\r\n\r\n<p><strong>בעוז</strong> - עומד בחזקו בדבר יראת ה\'.</p>\r\n<p><strong>ואיש דעת</strong> - בעל <strong>דעת</strong>, העדיף מחכם, הוא <strong>מאמץ כח</strong> בהוראה ומוסיף והולך.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מאמץ</strong> - מחזק.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 5 AND 1 LIMIT 1;

