UPDATE `sgulot` SET `mcudot` = '<p>- <strong>כי </strong>על-ידי <strong>תחבולות</strong> הדעת תוכל <strong>לעשות מלחמה</strong> מול היצר הרע המסית לעבירה,&#160; <strong>ו</strong>ה<strong>תשועה</strong> ממנו היא באה <strong>ברבות</strong> מעלת ה<strong>יועץ</strong> וגדולתו בדעת, ולזה מאמץ כוח בכל פעם כאשר יתחכם בדעת החכמה.\r\n\r\n\r\n\r\n</p>', `mqorot` = '<p>\r\n<strong>תחבולה</strong> <a href=\"http://localhost/tnk1/kma/qjrim1/txbla.html\">היא תוכנית פעולה המתייחסת לכל המקרים האפשריים, אסטרטגיה</a>;&#160;&#160; \r\n<strong>כי בתחבולות תעשה לך מלחמה</strong>         = כדי להצליח במלחמה יש לעשות מלחמה באמצעות תכנון אסטרטגי, ולשם כך יש צורך באנשי בטחון חכמים.\r\n</p>       \r\n<p>\r\n<strong>ותשועה ברוב יועץ</strong>         = ככל שיש יותר יועצים, יותר אנשים חכמים המסוגלים לתת עצות, יש יותר סיכוי לתשועה והצלה מאויב.</p>', `ecot` = '<p>\r\n<strong>תחבולה</strong> <a href=\"/tnk1/kma/qjrim1/txbla.html\">היא תוכנית פעולה המתייחסת לכל המקרים האפשריים, אסטרטגיה</a>;&#160;&#160; \r\n<strong>כי בתחבולות תעשה לך מלחמה</strong>         = כדי להצליח במלחמה יש לעשות מלחמה באמצעות תכנון אסטרטגי, ולשם כך יש צורך באנשי בטחון חכמים.\r\n</p>       \r\n<p>\r\n<strong>ותשועה ברוב יועץ</strong>         = ככל שיש יותר יועצים, יותר אנשים חכמים המסוגלים לתת עצות, יש יותר סיכוי לתשועה והצלה מאויב.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 6 AND 1 LIMIT 1;

