UPDATE `sgulot` SET `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק          <strong class=\"selflink\">י</strong>                        </p>\r\n</div>\r\n\r\n<p><strong>התרפית</strong> - אם היית <strong>מרפה</strong> ידך מחברך מלעזור לו <strong>ביום צרתו</strong>, אזי יהיה <strong>כחך צר</strong> ודחוק מלעזור לעצמך בבוא צרה עליך.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>התרפית</strong> - מלשון רפיון.</p>\r\n<p><strong>כחכה</strong> - כמו כחך.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 10 AND 1 LIMIT 1;

