UPDATE `sgulot` SET `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                 <strong class=\"selflink\">יז</strong>                 </p>\r\n</div>\r\n\r\n<p><strong>ובכשלו וגו\'</strong> - כפל הדבר במ\"ש.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 17 AND 1 LIMIT 1;

