UPDATE `sgulot` SET `mcudot` = '<p><strong>- כי&#160;לא תהיה </strong>ה<strong>אחרית</strong> כמו הראשית, כי סופם לאבדון, ו<strong>ידעך</strong> (יכבה) <strong>נר</strong> נשמתם.\r\n\r\n\r\n\r\n</p>', `tirgum` = '<p>- כי \r\n<strong>הרע</strong>   יכול להצליח רק בתקופה מסויימת ובמצב מסויים, אבל \r\n<strong>לא תהיה</strong>   לו הצלחה בתקופה \r\n<strong>מאוחרת</strong>   יותר שבה המצב ישתנה;&#160;&#160; ולכן ההצלחה של \r\n<strong>הרשעים</strong>   \r\n<strong>תדעך</strong> כמו \r\n<strong>נר</strong> הכבה כשנגמר לו הפתיל.</p>', `dquyot` = '<p>\r\n<strong>אחרית</strong> <a href=\"/tnk1/kma/qjrim1/axrit.html\">היא תקופה מאוחרת שבה המציאות אחרת.</a>&#160;&#160;&#160; \r\n<strong>נר רשעים ידעך</strong>     = כמו נר שבסופו של דבר כבה, כך גם הצלחתם של הרשעים תכבה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 20 AND 1 LIMIT 1;

