UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>והנה</strong>, על <strong>כל</strong> השדה <strong>עלה קמשונים וחרולים</strong> (מיני קוצים), כי בעבור העצלות לא חרש כראוי לחתוך שרשי הקוצים לבל יגדלו;&#160;&#160; <strong>וגדר </strong>(כותל)<strong> האבנים</strong> שהיה סביב <strong>נהרסה</strong> וניתצה, כי העצל סר השגחתו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 31 AND 1 LIMIT 1;

