UPDATE `sgulot` SET `mcudot` = '\r\n\r\n<p>- <strong>ו</strong>כאשר <strong>חזיתי</strong> (ראיתי) <strong>אני</strong> את כל זה, <strong>שמתי לבי</strong> להבין מה העצלות עושה, וכאשר <strong>ראיתי - לקחתי מוסר</strong> לעזוב דרך העצלות -</p>', `tirgum` = '<p>\r\n- <strong>ואחזה</strong>  (ראיתי בחזון נבואי את משמעות המראה), \r\n<strong>אנוכי אשית ליבי</strong>, \r\n<strong>ראיתי, לקחתי מוסר</strong>  (הפקתי לקחים מהצרה שבאה עליו) -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 32 AND 1 LIMIT 1;

