UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>פן יראה ה\', ורע בעיניו</strong> על שאתה שמח בתקלתו, <strong>ו</strong>עליך <strong>ישוב האף מעליו</strong>, כי ממנו יסיר ועליך יחול.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 18 AND 1 LIMIT 1;

