UPDATE `sgulot` SET `mcudot` = '<p>בתחילה <strong>הכן מלאכתך בחוץ</strong>, והם מלאכת שדה וכרם;&#160;&#160;&#160; <strong>ועתדה</strong> ( הזמן) <strong>לך בשדה</strong> די מחסורך;&#160; <strong>אחר</strong> זה <strong>בנה ביתך</strong> לשבת בו, כי אם לא תכין די מחסורך לא תוכל שבת בית, כי תהיה נודד ללחם.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 27 AND 1 LIMIT 1;

