UPDATE `sgulot` SET `mcudot` = '<p><strong>מחשב להרע</strong>, עם כי לא עשה הרעה, סוף יגולה קלונו, <strong>ויקראו לו</strong>: <strong>בעל מזימות</strong> (מחשבות רעות).</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 8 AND 1 LIMIT 1;

