UPDATE `sgulot` SET `mcudot` = '<p><strong>זימת</strong> (מחשבת) <strong>איוולת</strong> ורשע נחשבת ל<strong>חטאת</strong>, עם כי לא עשה מעשה; &#160;&#160; וה<strong>ליצנות מתעבת את האדם</strong> בעיני כל, עם כי אין בה כי-אם עקימת שפתים.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 24 AND `verse_number` = 9 AND 1 LIMIT 1;

