UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>כבוד ה\'</strong> הוא <strong>להסתיר דבר</strong> גדולת רוממותו, כי אי-אפשר לבוא עד התכלית ומוטב להמעיט מלהרבות, וכמו שנאמר \"<strong>לך דומיה תהילה</strong>\".&#160;&#160; אבל <strong>כבוד מלכים</strong> הוא <strong>לחקור</strong> (לחפש ולדרוש)<strong> דבר</strong> גדולת רוממותם, הואיל ואפשר לבוא עד התכלית, אם-כן הממעיט יחשב כאילו כבר בא עד התכלית, ומוטב להרבות מלהמעיט.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 2 AND 1 LIMIT 1;

