SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '<p>יראה כי מתחלת הספר עד הנה היה מועתק ביד הכל, ומכאן עד סופו לא היה מועתק כי-אם בידי אנשי חזקיה מלך יהודה, אשר העתיקו הדברים האלה מספרי שלמה אשר נזדמן להם.&#160; </p><p>ולזה אמר <strong>גם אלה משלי שלמה</strong>, רוצה לומר: עם כי לא נמצאו ביד הכל, מכל-מקום גם אלה דבריו <strong>אשר העתיקו אנשי חזקיה</strong>, והמה נאמנים על הדבר שמפי שלמה יצאו.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 1 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '<p><strong>כבוד ה\'</strong> הוא <strong>להסתיר דבר</strong> גדולת רוממותו, כי אי-אפשר לבוא עד התכלית ומוטב להמעיט מלהרבות, וכמו שנאמר \"<strong>לך דומיה תהילה</strong>\".&#160;&#160; אבל <strong>כבוד מלכים</strong> הוא <strong>לחקור</strong> (לחפש ולדרוש)<strong> דבר</strong> גדולת רוממותם, הואיל ואפשר לבוא עד התכלית, אם-כן הממעיט יחשב כאילו כבר בא עד התכלית, ומוטב להרבות מלהמעיט.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 2 AND 1 LIMIT 1;

