UPDATE `sgulot` SET `mcudot` = '\r\n\r\n<p><strong>אל תתהדר</strong> - <strong>אל</strong> תעשה עצמך <strong>הדר</strong> ויפה להתגאות כאשר תעמוד <strong>לפני המלך</strong>, <strong>ואל</strong> תדחוק עצמך <strong>לעמוד במקום שהגדולים</strong> והחשובים עומדים שם -</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 6 AND 1 LIMIT 1;

