UPDATE `sgulot` SET `mcudot` = '\r\n<p>כמו ציורי <strong>תפוחי זהב</strong> החקוקים <strong>על משכיות </strong>(מכסה)<strong> של כסף</strong>, שהוא דבר מפואר, כן הוא <strong>דבר המדובר על אופניו</strong>, ר\"ל דבר הראוי לעניינו וחוזר אליו, כאופַן-העגלה המתגלגל וחוזר למקומו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 11 AND 1 LIMIT 1;

