UPDATE `sgulot` SET `mcudot` = '<p><strong>אל תצא</strong> (תתגרה) <strong>במריבה מהר</strong>, עד אשר תחקור אם יש בזה דבר אשמה על שכנגדך, כי <strong>פן לא תדע מה תעשה</strong> באחרית המריבה, <strong>ב</strong>עת <strong>יכלים אותך רעך</strong> לומר \'הלא אין אשמה בידי\', והנה הסכלת עשו להתקוטט עד לא חקרת האמת -</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 8 AND 1 LIMIT 1;

