UPDATE `sgulot_hqblot` SET `verse_text` = 'ה\' קָנָנִי רֵאשִׁית דַּרְכּוֹ,קֶדֶם מִפְעָלָיו מֵאָז;' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 22 AND `verse_text` = 'ה\' קָנָנִי רֵאשִׁית דַּרְכּוֹ,\r\nקֶדֶם מִפְעָלָיו מֵאָז;' AND `hqblot` = '<p>בתורה נאמר <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t0101.htm#1\">בראשית א1</a>: \"<q class=\"psuq\">בְּ<strong>רֵאשִׁית</strong> בָּרָא אֱלֹהִים אֵת הַשָּׁמַיִם וְאֵת הָאָרֶץ</q>\" (<a href=\"http://he.wikisource.org/wiki/%D7%91%D7%99%D7%90%D7%95%D7%A8:%D7%91%D7%A8%D7%90%D7%A9%D7%99%D7%AA_%D7%90_%D7%90\">פירוט</a>), כלומר ראשית הבריאה היא השמיים והארץ. אולם בפסוקנו נאמר שראשית הבריאה היא החכמה, והיא נבראה עוד לפני השמים והארץ! ולכן תרגמו חז\"ל את המילה \"<q class=\"psuq\">בראשית</q>\" - \"<q class=\"mfrj\">בחוכמתא</q>\", <strong>ב</strong>אמצעות החכמה שהיא היצירה ה<strong>ראשית</strong><small class=\"small\"> (ראו רמב\"ן שם בסוף דבריו. ורש\"י שם דרש באופן אחר)</small>.</p><p>חכמת ה\' בבריאת העולם מתוארת גם ב<a class=\"psuq\" href=\"/tnk1/prqim/t26a4.htm#24\">תהלים קד24</a>: \"<q class=\"psuq\">מָה רַבּוּ מַעֲשֶׂיךָ ה\', כֻּלָּם \r\n<strong>בְּחָכְמָה עָשִׂיתָ</strong>, מָלְאָה הָאָרֶץ קִנְיָנֶךָ</q>\" וב<a class=\"psuq\" href=\"/tnk1/prqim/t2692.htm#6\">תהלים צב6</a>: \"<q class=\"psuq\">מַה גָּדְלוּ מַעֲשֶׂיךָ ה\', מְאֹד \r\n<strong>עָמְקוּ מַחְשְׁבֹתֶיךָ</strong></q>\". </p>' AND 1 LIMIT 1;

