UPDATE `sgulot_hqblot` SET `verse_text` = 'הֲלֹא חָכְמָה תִקְרָא, וּתְבוּנָה תִּתֵּן קוֹלָהּ;' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 1 AND `verse_text` = 'הֲלֹא חָכְמָה תִקְרָא,וּתְבוּנָה תִּתֵּן קוֹלָהּ;' AND `hqblot` = '<p>החכמה והתבונה קוראות לך, אבל גם את/ה צריך/ה לקרוא להן:</p><p><a class=\"psuq\" href=\"/tnk1/prqim/t2802.htm#3\">משלי ב3</a>: \"<q class=\"psuq\">כִּי אִם \r\n<strong>לַבִּינָה תִקְרָא,</strong> לַתְּבוּנָה \r\n<strong>תִּתֵּן קוֹלֶךָ</strong></q>\" (<a href=\"/tnk1/ktuv/mj/02-03.html\">פירוט</a>)<a class=\"psuq\" href=\"/tnk1/prqim/t2807.htm#4\"></a></p><p><a class=\"psuq\" href=\"/tnk1/prqim/t2807.htm#4\">משלי ז4</a>: \"<q class=\"psuq\">אֱמֹר לַחָכְמָה \'אֲחֹתִי אָתְּ\', וּ\'מֹדָע\' \r\n<strong>לַבִּינָה תִקְרָא</strong></q>\" (<a href=\"/tnk1/ktuv/mj/07-04.html\">פירוט</a>)</p>' AND 1 LIMIT 1;

