SET character_set_connection=utf8;
UPDATE `sgulot_hqblot` SET `verse_text` = 'כִּי טוֹבָה חָכְמָה מִפְּנִינִים, וְכָל חֲפָצִים לֹא יִשְׁווּ בָהּ.' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 11 AND `verse_text` = 'כִּי טוֹבָה חָכְמָה מִפְּנִינִים,וְכָל חֲפָצִים לֹא יִשְׁווּ בָהּ.' AND `hqblot` = '<p>פסוקים 10-11 דומים מאד ל<a class=\"psuq\" href=\"/tnk1/prqim/t2803.htm#15\">משלי ג14-15</a>: \"<q class=\"psuq\">כִּי טוֹב סַחְרָהּ מִסְּחַר \r\n<strong>כָּסֶף</strong>, \r\n<strong>וּמֵחָרוּץ</strong> תְּבוּאָתָהּ.</q> \r\n<q class=\"psuq\">יְקָרָה הִיא \r\n<strong>מפניים[מִפְּנִינִים]</strong>, וְכָל \r\n<strong>חֲפָצֶיךָ</strong> לֹא יִשְׁווּ בָהּ</q>\" (<a href=\"/tnk1/ktuv/mj/03-15.html\">פירוט</a>).</p><p style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\">\r\nבפרק ג נאמר שהחָכְמָה טוֹבָהּ יותר מ\"<q class=\"psuq\">כל חֲפָצֶיךָ</q>\", ובפרק ח נאמר שהחכמה טובה יותר מ\"<q class=\"psuq\">כל חֲפָצִים</q>\". וחכמי התלמוד הבדילו ביניהם:</p><p style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\">\r\n1. בתלמוד הבבלי: \"<q class=\"mfrj\">ר\' יונתן בן עסמיי ור\' יהודה בן גרים...\r\nכתיב\r\n<strong>יקרה היא מפנינים וכל חפציך לא ישוו בה</strong>                    - הא חפצי שמים ישוו בה, וכתיב\r\n<strong>כל חפצים לא ישוו בה</strong>                    - דאפילו חפצי שמים לא ישוו בה! - כאן במצוה שאפשר לעשותה על ידי אחרים, כאן במצוה שאי אפשר לעשותה על ידי אחרים</q>\" \r\n<small>(<a href=\"http://he.wikisource.org/wiki/%D7%91%D7%99%D7%90%D7%95%D7%A8:%D7%91%D7%91%D7%9C%D7%99_%D7%9E%D7%95%D7%A2%D7%93_%D7%A7%D7%98%D7%9F_%D7%93%D7%A3_%D7%98#.D7.A2.D7.9E.D7.95.D7.93_.D7.91\">תלמוד בבלי, מועד קטן ט.</a>)</small>. כלומר:</p><ul><li style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\"><strong>חפציך</strong>    הם רצונותיו האישיים של האדם, כגון כסף, הצלחה\r\nחומרית, כבוד וכו\'. מושג דומה מופיע ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t1058.htm#13\">ישעיהו נח13</a>: \"<q class=\"psuq\">אִם תָּשִׁיב מִשַּׁבָּת רַגְלֶךָ, עֲשׂוֹת \r\n<strong>חֲפָצֶיךָ</strong> בְּיוֹם קָדְשִׁי; וְקָרָאתָ לַשַּׁבָּת עֹנֶג, לִקְדוֹשׁ ה\' מְכֻבָּד; וְכִבַּדְתּוֹ מֵעֲשׂוֹת דְּרָכֶיךָ, מִמְּצוֹא \r\n<strong>חֶפְצְךָ</strong> וְדַבֵּר דָּבָר</q>\" וחז\"ל למדו מכאן שאסור לעסוק בענייני חול ביום השבת. הפסוק בפרק ג אומר, שלימוד החכמה והתבונה טוב\r\nומועיל יותר מהעיסוק בענייני חול.\r\n</li><li style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\"><strong>חפצים</strong>    הוא מושג כללי יותר - הם כוללים גם את רצונותיו\r\nהאישיים של האדם וגם \"רצונות\" רוחניים יותר כמו קיום מצוות ה\', שנאמר\r\nעליהן ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t1109.htm#23\">ירמיהו ט23</a>: \"<q class=\"psuq\">...כִּי אֲנִי ה\' עֹשֶׂה חֶסֶד מִשְׁפָּט וּצְדָקָה בָּאָרֶץ כִּי בְאֵלֶּה \r\n<strong>חָפַצְתִּי</strong> נְאֻם ה\'</q>\"; הפסוק בפרק ח אומר,\r\nשהמוסר והדעת טובים ונבחרים יותר מכל החפצים - אפילו מקיום המצוות.</li></ul><p>ריב\"ע וריב\"ג פירשו, שהפסוק בפרק ח מתייחס רק ל<a href=\"http://localhost/tnk1/klli/mdrjim/elaya.html\" class=\"mamr dor1\">מצוה שאפשר לעשותה על-ידי אחרים</a>, מצוה שהיא בגדר התנדבות שגם אנשים אחרים יכולים לבצע. לדבריהם, מי שיכול ללמוד תורה צריך לעשות זאת, ולהשאיר את המצוות ההתנדבותיות לאלה שאינם יכולים ללמוד.&#160;&#160; הפסוק בפרק ג מתייחס למצוה שאי-אפשר לעשותה על-ידי אחרים, ולכן גם מי שיכול ללמוד תורה צריך לסגור את הספר וללכת לקיים את המצוה. אולם, לא ברור לי איך הגיעו למסקנה שפרק ג מתייחס&#160; דווקא למצוה שאי-אפשר לעשותה על-ידי אחרים; לא מצאתי לכך רמז בפרק.</p><p style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\">2. בתלמוד הירושלמי: \"<q class=\"mfrj\">רבי ברכיה ורבי חייא דכפר דחומין: חד אמר \'אפילו כל העולם כולו אינו\r\nשוה לדבר אחד של תורה\', וחד אמר \'אפילו כל מצותיה של תורה אינן שוות לדבר\r\nאחד מן התורה\'. רבי תנחומא ורבי יוסי בן זימרא, חד אמר כהדא וחד אמר כהדא.\r\nרבי אבא אבוי דרבי אבא בר מרי בשם ר\' אחא: כתוב אחד אומר\r\n<strong>וכל חפצים לא ישוו בה</strong>                    וכתוב אחד אומר\r\n<strong>וכל חפציך לא ישוו בה</strong>                    - חפצים אלו אבנים טובות ומרגליות, חפציך אלו דברי תורה, דכתיב כי באלה חפצתי נאם ה\'</q>\" \r\n<small>(<a href=\"http://kodesh.snunit.k12.il/b/r/r1201_004a.htm\">תלמוד ירושלמי, פיאה ד.</a>)</small>. &#160; הפירוש למילים \r\n  <strong>חפציך</strong>    ו<strong>חפצים</strong>    מנוגד לפירוש שקראנו בבבלי, ונראה גם מנוגד למשמעות המילולית שלהן כפי שהסברנו למעלה, וייתכן שיש טעות דפוס וצריך לגרוס הפוך -\r\n  \"<q class=\"mfrj\">חפציך אלו אבנים טובות&#160; ומרגליות, חפצים אלו דברי תורה</q>\".&#160;&#160; בכל מקרה, חכמי א\"י לא ניסו ליישב בין שני הפרקים אלא השאירו את העניין שנוי במחלוקת - האם לימוד תורה דוחה רק ענייני חול, או גם מצוות?</p>                                       \r\n<p>\r\n     לענ\"ד, כדי להסביר את ההבדל בין הפסוקים, יש לבדוק למי פונה כל פרק.\r\n</p>         <ul><li>פרק ג פונה אל האנשים הפשוטים, התלמידים; כך ניתן להסיק מהפניה \r\n  <strong>בני</strong>    (בן = תלמיד), שמופיעה בו שלוש פעמים - בפסוק 1, 11 ו-21.</li><li>פרק ח פונה אל האישים החשובים, המנהיגים, שתפקידם ללמד; כך ניתן להסיק מרשימת \"בעלי התפקידים\" בפרק (פסוקים 15-16) ואולי גם מפתיחת הפרק (פסוקים 1-4; ראו במבוא). תפקידם של אותם אנשים הוא ללמד, כפי שכתוב בפסוק הבא (פסוק 5): \"<q class=\"psuq\">הָבִינוּ פְתָאיִם עָרְמָה...</q>\" - עזרו לפתיים להבין את סודות העורמה (<a href=\"/tnk1/ktuv/mj/08-05.html\">פירוט</a>).</li></ul>\r\n         \r\n<p>\r\nהבדל זה מתבטא גם בפסוקים שהבאנו למעלה:\r\n<br />\r\n          \r\n</p>         <ul><li>בפרק ג נאמר \"<q class=\"psuq\">כִּי טוֹב\r\n<strong>סַחְרָהּ</strong>                    מִסְּחַר כָּסֶף, וּמֵחָרוּץ\r\n<strong>תְּבוּאָתָהּ</strong></q>\" - הדגש הוא על התועלת הכלכלית שאפשר להפיק מהחכמה - מסחר ותבואה;</li><li>ובפרק ח נאמר: \"<q class=\"psuq\">קְחוּ\r\n<strong>מוּסָרִי</strong>                    וְאַל כָּסֶף,\r\n<strong>וְדַעַת</strong>                    מֵחָרוּץ נִבְחָר</q>\" - הדגש הוא על התועלת הרוחנית שאפשר להפיק מהחכמה - מוסר ודעת.\r\n</li></ul>\r\n<p>לפי זה, ניתן להציע שני הסברים להבדלים בין הפרקים:\r\n</p>\r\n         \r\n<p>3. פרק ג מתייחס ללימוד תורה של אדם מן השורה, ופרק ח מתייחס ללימוד תורה של מנהיג.\r\n    \r\n</p>         \r\n<p>התורה מדגישה במיוחד את חובתו של המלך ללמוד תורה, <a class=\"psuq\" href=\"/tnk1/prqim/t0517.htm#18\">דברים יז18-19</a>: \"<q class=\"psuq\">וְהָיָה\r\n כְשִׁבְתּוֹ עַל כִּסֵּא מַמְלַכְתּוֹ, וְכָתַב לוֹ אֶת מִשְׁנֵה הַתּוֹרָה הַזֹּאֶת \r\nעַל סֵפֶר מִלִּפְנֵי הַכֹּהֲנִים הַלְוִיִּם. <strong>וְהָיְתָה עִמּוֹ וְקָרָא בוֹ כָּל \r\nיְמֵי חַיָּיו, לְמַעַן יִלְמַד לְיִרְאָה אֶת ה\' אֱלֹהָיו</strong>, לִשְׁמֹר אֶת כָּל \r\nדִּבְרֵי הַתּוֹרָה הַזֹּאֶת וְאֶת הַחֻקִּים הָאֵלֶּה לַעֲשֹׂתָם.</q>\" כל אדם \r\nצריך לכתוב לעצמו ספר תורה, והמלך צריך לכתוב לעצמו ספר תורה נוסף - שיהיה \r\nעמו בכל מקום שהוא נמצא בו, כך שיוכל לקרוא וללמוד בכל מצב, וליישם את חוקי\r\n התורה בכל מצב. \r\n</p><p>גם ליהושע, לאחר שהתמנה למנהיג ישראל, נאמר, <a class=\"psuq\" href=\"/tnk1/prqim/t0601.htm#8\">יהושע א8</a>: \"<q class=\"psuq\">לֹא יָמוּשׁ סֵפֶר הַתּוֹרָה הַזֶּה מִפִּיךָ, \r\n<strong>וְהָגִיתָ בּוֹ יוֹמָם וָלַיְלָה</strong>, לְמַעַן תִּשְׁמֹר לַעֲשׂוֹת כְּכָל הַכָּתוּב בּוֹ; כִּי אָז תַּצְלִיחַ אֶת דְּרָכֶךָ, וְאָז תַּשְׂכִּיל</q>\". דרך לימוד התורה, ה\' מנהיג את המנהיג.\r\n</p>         \r\n<p>מכאן ניתן ללמוד, שהמלך לא צריך לנצל את זמנו הפנוי להתנדבות, אלא רק ללימוד; ומכאן שעבור המלך, לימוד תורה הוא הדבר החשוב ביותר - \"<q class=\"psuq\">כל חפצים לא ישוו בה</q>\" - אפילו מצוות של התנדבות לא ישוו בה. כך הדבר גם לגבי המנהיגים האחרים שנזכרו בפרק ח - רוזנים, שרים ושופטים.\r\n    \r\n</p>         \r\n<p>אולם, עבור האדם הפשוט \"<q class=\"psuq\">כל חפציך לא ישוו בה</q>\" - \r\nענייני חול אינם שווים בחשיבותם לעיסוק בחכמה, אבל מצוות של התנדבות כן \r\nשוות, והאדם יכול לבחור במה הוא רוצה להשקיע את זמנו הפנוי - אם בלימוד \r\nחכמה או בהתנדבות.&#160;</p> \r\n<p>לאחר שאדם הוכיח את עצמו, במעשים של התנדבות, שהוא מוכן למסור את נפשו למען הזולת, ייתכן שה\' ימנה אותו לתפקיד של מנהיג  (כמו למשל \r\n<a href=\"/tnk1/tora/jmot/jm-02-mje.html\">משה הצעיר</a>), ואז יקדיש את זמנו הפנוי ללימוד, על-מנת שיוכל להנהיג בצורה הטובה ביותר.</p>         \r\n         \r\n         \r\n<p>4. ייתכן שהפסוק מפרק ג מתייחס לחכמה שהאדם\r\n<strong>לומד</strong>                    לעצמו, והפסוק מפרק ח מתייחס לחכמה שהאדם\r\n<strong>מלמד</strong>                    אחרים:\r\n</p>                    <ul><li style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\">הפסוק בפרק ג אומר לאדם, שעדיף שיִלְמַד חכמה מאשר שיעסוק בהגשמת\r\n  <strong>חפציו</strong>                    האישיים;</li><li style=\"background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;\">והפסוק בפרק ח אומר לאדם, שעדיף שיְלַמֵּד חכמה מאשר שיעסוק בהגשמת\r\n  <strong>חפצים</strong>                    כלשהם, אפילו מצוות אחרות.\r\n  <strong>האישים</strong>,                   אלו שיודעים ללמד, חייבים ללמד, והדבר חשוב אפילו יותר מאשר קיום מצוות של התנדבות; \r\n  <a href=\"/tryg/mamr/sdr_cibur.html\">מצוות המזכות את הרבים חשובות יותר ממצוות של אדם פרטי</a>.</li></ul><p>                  לפי זה, הפסוקים אינם קובעים סדר עדיפויות בין קיום \r\nמצוות של התנדבות לבין לימוד עצמי - אדם הלומד תורה לעצמו בלבד, רשאי לבחור\r\n עד כמה הוא רוצה להשקיע בלימוד לעומת התנדבות, והטוב ביותר הוא שישלב \r\nביניהם, שהרי כל מטרתו של לימוד התורה הוא להביא לידי מעשה, <a class=\"psuq\" href=\"/tnk1/prqim/t0505.htm#1\">דברים ה1</a>: \"<q class=\"psuq\">וּלְמַדְתֶּם אֹתָם, וּשְׁמַרְתֶּם לַעֲשֹׂתָם</q>\".\r\n</p>\r\n\r\n<p>מסקנה זו מתאימה לדברי חז\"ל בכמה מקומות, למשל ב<a href=\"http://kodesh.snunit.k12.il/b/l/l2901_003a.htm\">בבלי מגילה ג</a> נאמר, ש<strong>לימוד תורה של רבים</strong>               חשוב יותר מאשר הקרבת \r\n  <strong>קרבנות</strong>, שהיא מצוה מעשית; אך הקרבת הקרבנות חשובה יותר מאשר \r\n  <strong>לימוד תורה של יחיד</strong>               (<a href=\"/tnk1/klli/mdrjim/mg03.html\">פירוט</a>).</p> \r\n<p>לאחר שהאדם הוכיח את עצמו במעשים, ראוי שיתחיל ללמד אחרים, כך שיהיה \"<q class=\"mfrj\">נאה דורש ונאה מקיים</q>\".\r\n<br />\r\n</p>\r\n<p> \r\nלסיכום (וכמובן בלי לפסוק הלכה), נראה שרוב האנשים, שאינם מנהיגי ציבור \r\nואינם עוסקים בהוראה, צריכים לשלב בין לימוד לבין התנדבות, כל אחד לפי \r\nיכולתו ואופיו; אך מנהיגי ציבור צריכים להשקיע את רוב זמנם בלימוד מצוות המנהיגות, ואנשים \r\nשיכולים ללמד צריכים להשקיע את רוב זמנם בהוראה, כי בשני המקרים הללו, \r\nהחכמה חשובה יותר אפילו ממצוות התנדבותיות - \"<q class=\"psuq\">וכל חפצים לא ישוו בה</q>\".\r\n</p>\r\n         \r\n\r\n<p class=\"future\"><span><strong>אשת חיל חשובה כמו לימוד תורה</strong> -</span> במאמרנו השוינו בין לימוד תורה לקיום מצוות מעשיות, אולם יש עניין אחד בעולם המעשה שערכו גדול לפחות כמו לימוד תורה: \r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2831.htm#10\">משלי לא10</a>: \"<q class=\"psuq\">אֵשֶׁת חַיִל מִי יִמְצָא? וְרָחֹק \r\n<strong>מִפְּנִינִים</strong> מִכְרָהּ</q>\" (<a href=\"/tnk1/ktuv/mj/31-10.html\">פירוט</a>). ערכה של אשת חיל מתואר בביטוי \"<q class=\"psuq\">רחוק מפנינים</q>\", דומה מאד לביטויים המתארים את ערכה של החכמה - \"<q class=\"psuq\">יקרה היא מפנינים</q>\", \"<q class=\"psuq\">כי טובה חכמה מפנינים</q>\".\r\n        \r\n</p><p class=\"future\"><strong>לימוד תורה לעומת קיום מצוות בחסידות</strong> - הרש\"ז מלאדי מסביר מדוע במקרים מסויימים קיום מצוות קודם ללימוד תורה, ובמקרים אחרים לימוד תורה קודם לקיום מצוות: \"<q class=\"mfrj\">מבטלין\r\n תלמוד תורה לקיום מצוה מעשיית כשאי-אפשר לעשותה על-ידי אחרים, משום כי זה \r\nכל האדם ותכלית בריאתו וירידתו לעולם הזה, להיות לו ית\' דירה בתחתונים \r\nדווקא, לאהפכא חשוכא לנהורא, וימלא כבוד ה\' את כל הארץ הגשמית דייקא, וראו \r\nכל בשר יחדיו, כנ\"ל;\r\n</q></p>        \r\n<p class=\"future\">\r\n<q class=\"mfrj\">מה שאין כן כשאפשר לעשותה ע\"י אחרים, אין מבטלין תלמוד \r\nתורה, אף שכל התורה אינה אלא פירוש המצוות מעשיות, והיינו משום שהיא בחינת \r\nחב\"ד של אין-סוף ברוך הוא, ובעסקו בה ממשיך עליו אור אין-סוף ברוך הוא ביתר\r\n שאת והארה גדולה לאין קץ מהארה והמשכה על-ידי פיקודין שהן אברים דמלכא.</q>\" \r\n<small>(<a href=\"http://he.wikisource.org/wiki/%D7%AA%D7%A0%D7%99%D7%90_%D7%97%D7%9C%D7%A7_%D7%90_%D7%9C%D7%96\">תניא חלק א לז</a>)</small>.\r\n</p>        \r\n<p class=\"future\">תכלית הבריאה היא להאיר את אור ה\' בעולם החומרי-החשוך, דבר שנעשה על-ידי\r\n קיום מצוות. כל אדם נשלח לעולם הזה כדי לבצע חלק מהתכלית הזאת, ולהאיר את \r\nאור ה\' בחלק מסויים מהעולם ששייך לו. לכן ישנה עדיפות עליונה לכך שהאדם \r\nיקיים את המצוות הספציפיות שמוטלות עליו - מצוות שאי-אפשר לעשותן על-ידי \r\nאחרים - שהרי בכך הוא ממלא את תפקידו, ואם הוא לא יעשה זאת, החלק שלו בעולם\r\n הזה לא יתוקן. \r\n</p><p class=\"future\">        \r\nאולם, מצוות שאפשר לעשותן על-ידי אחרים הן כבר מעבר לתפקידו, ולכן יש \r\nלהן עדיפות נמוכה יותר, ואם יש לאדם בחירה, עדיף שיבחר בתלמוד תורה, שמקרב \r\nאותו אל ה\' יותר מאשר קיום מצוות.\r\n</p>' AND 1 LIMIT 1;

