UPDATE `sgulot_hqblot` SET `verse_text` = 'בִּי מְלָכִים יִמְלֹכוּ, וְרוֹזְנִים יְחֹקְקוּ צֶדֶק,' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 15 AND `verse_text` = 'בִּי מְלָכִים יִמְלֹכוּ,וְרוֹזְנִים יְחֹקְקוּ צֶדֶק,' AND `hqblot` = '<p>במדינה מתוקנת יש שלוש רשויות: הרשות השופטת, הרשות המחוקקת והרשות המבצעת, ולפעמים יש גם רשות רביעית ייצוגית - כמו נשיא מדינת ישראל או מלך אנגליה.&#160; ארבע רשויות אלו נרמזו ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t1033.htm#22\">ישעיהו לג22</a>: \"<q class=\"psuq\">כִּי ה\' שֹׁפְטֵנוּ, ה\' מְחֹקְקֵנוּ, ה\' מַלְכֵּנוּ, הוּא יוֹשִׁיעֵנוּ</q>\" (<a href=\"http://localhost/tnk1/nvia/yjayhu/yj-33-22.html\">פירוט</a>).&#160;&#160; הבינה חיונית לתיפקודן של כל ארבעת הרשויות:\r\n</p>\r\n\r\n\r\n<ul><li>\r\n<strong>בי מלכים ימלוכו</strong>     - הרשות הייצוגית.</li><li>\r\n<strong>ורוזנים יחוקקו צדק</strong>     - הרשות המחוקקת.</li><li>\r\n<strong>בי שרים ישרו</strong>     - הרשות המבצעת.\r\n</li><li>\r\n<strong>ונדיבים כל שופטי צדק</strong>     - הרשות השופטת.</li></ul>\r\n\r\n' AND 1 LIMIT 1;

