UPDATE `sgulot_hqblot` SET `verse_text` = 'וְעַתָּה, בָנִים, שִׁמְעוּ לִי, וְאַשְׁרֵי דְּרָכַי יִשְׁמֹרוּ:' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 32 AND `verse_text` = 'וְעַתָּה, בָנִים, שִׁמְעוּ לִי,וְאַשְׁרֵי דְּרָכַי יִשְׁמֹרוּ:' AND `hqblot` = '<p class=\"future\">המילה <strong>אשרי</strong> נזכרת בפסוקים רבים. אחד מהם, המתייחס בפירוש לחכמה, נזכר בדברי מלכת שבא אל שלמה, <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t09a10.htm#8\">מלכים א י8</a>: \"<q class=\"psuq\"><strong>אַשְׁרֵי</strong> אֲנָשֶׁיךָ, <strong>אַשְׁרֵי</strong> עֲבָדֶיךָ אֵלֶּה הָעֹמְדִים לְפָנֶיךָ תָּמִיד הַשֹּׁמְעִים אֶת חָכְמָתֶךָ</q>\". </p>' AND 1 LIMIT 1;

