UPDATE `sgulot_hqblot` SET `verse_text` = 'לִי עֵצָה וְתוּשִׁיָּה.אֲנִי בִינָה:לִי גְבוּרָה,' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 14 AND `verse_text` = 'לִי עֵצָה וְתוּשִׁיָּה.\r\n\r\nאֲנִי בִינָה:\r\nלִי גְבוּרָה,' AND `hqblot` = '<p>מדוע החכמה והבינה משבחות את עצמן? הרי זה מנוגד ל<a class=\"psuq\" href=\"/tnk1/prqim/t2827.htm#2\">משלי כז2</a>: \"<q class=\"psuq\">יְהַלֶּלְךָ זָר וְלֹא פִיךָ, נָכְרִי וְאַל שְׂפָתֶיךָ</q>\" (<a href=\"/tnk1/ktuv/mj/27-02.html\">פירוט</a>)!</p><p>מסתבר שהחכמה והבינה כאן הן משל - חכמתו ובינתו של האדם מפרסמות את האדם מבלי שהוא צריך להלל את עצמו, כמו ב<a class=\"psuq\" href=\"/tnk1/prqim/t3108.htm#1\">קהלת ח1</a>: \"<q class=\"psuq\">חָכְמַת אָדָם תָּאִיר פָּנָיו</q>\".</p><p class=\"future\">קשר בין גבורה לחכמה נמצא גם ב\r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2821.htm#22\">משלי כא22</a>: \"<q class=\"psuq\">עִיר \r\n<strong>גִּבֹּרִים</strong> עָלָה \r\n<strong>חָכָם</strong>, וַיֹּרֶד עֹז מִבְטֶחָה</q>\" (<a href=\"/tnk1/ktuv/mj/21-22.html\">פירוט</a>).</p>\r\n' AND 1 LIMIT 1;

