UPDATE `sgulot_hqblot` SET `verse_text` = 'מֵעוֹלָם נִסַּכְתִּי,מֵרֹאשׁ, מִקַּדְמֵי אָרֶץ.' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 23 AND `verse_text` = 'מֵעוֹלָם נִסַּכְתִּי,\r\nמֵרֹאשׁ, מִקַּדְמֵי אָרֶץ.' AND `hqblot` = '<p>בפסוקים אחרים בספר משלי נאמר שיש חכמה בבעלי החיים, \r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2806.htm#6\">משלי ו6</a>: \"<q class=\"psuq\">לֵךְ אֶל \r\nנְמָלָה עָצֵל, רְאֵה דְרָכֶיהָ וַחֲכָם</q>\" (<a href=\"http://localhost/tnk1/ktuv/mj/06-06.html\">פירוט</a>); ובצמחים, \r\n<a class=\"psuq\" href=\"http://localhost/_script/wymeditor/wymeditor/prqim/t2827.htm#18\">משלי כז18</a>: \"<q class=\"psuq\">נֹצֵר \r\nתְּאֵנָה יֹאכַל פִּרְיָהּ, וְשֹׁמֵר אֲדֹנָיו יְכֻבָּד</q>\" (<a href=\"http://localhost/_script/wymeditor/wymeditor/iframe/default/mj-27-18.html\">פירוט</a>). בפסוקים שלנו נאמר שיש חכמה גם בטבע הדומם של הארץ - הרים וגבעות, מעיינות ותהומות, שמיים ים וחוף.</p>' AND 1 LIMIT 1;

