UPDATE `sgulot_hqblot` SET `verse_text` = 'וְחֹטְאִי חֹמֵס נַפְשׁוֹ,כָּל מְשַׂנְאַי אָהֲבוּ מָוֶת.' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 36 AND `verse_text` = 'וְחֹטְאִי חֹמֵס נַפְשׁוֹ,\r\nכָּל מְשַׂנְאַי אָהֲבוּ מָוֶת.' AND `hqblot` = '<p class=\"future\">\r\n<strong>חומס נפשו</strong>  - גם בפרקים קודמים נאמר, שמי שנוהג בניגוד לעצות החכמה, מסכן את נפשו:<a class=\"psuq\" href=\"/tnk1/prqim/t2806.htm#32\"></a></p><p class=\"future\"><a class=\"psuq\" href=\"/tnk1/prqim/t2806.htm#32\">משלי ו32</a>: \"<q class=\"psuq\">נֹאֵף אִשָּׁה חֲסַר לֵב, מַשְׁחִית \r\n<strong>נַפְשׁוֹ</strong> הוּא יַעֲשֶׂנָּה</q>\" (<a href=\"/tnk1/ktuv/mj/06-32.html\">פירוט</a>)<a class=\"psuq\" href=\"/tnk1/prqim/t2807.htm#23\"></a></p><p class=\"future\"><a class=\"psuq\" href=\"/tnk1/prqim/t2807.htm#23\">משלי ז23</a>: \"<q class=\"psuq\">עַד יְפַלַּח חֵץ כְּבֵדוֹ, כְּמַהֵר צִפּוֹר אֶל פָּח, וְלֹא יָדַע כִּי \r\n<strong>בְנַפְשׁוֹ</strong> הוּא</q>\" (<a href=\"/tnk1/ktuv/mj/07-23.html\">פירוט</a>)</p><p class=\"future\"> ראו עוד על \r\n<a href=\"/tnk1/kma/qjrim1/sna.html\">שנאה לאנשים רעים או תכונות רעות</a>.</p>' AND 1 LIMIT 1;

