UPDATE `sgulot_hqblot` SET `verse_text` = 'הָבִינוּ פְתָאיִם עָרְמָה, וּכְסִילִים - הָבִינוּ לֵב!' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 5 AND `verse_text` = 'הָבִינוּ פְתָאיִם עָרְמָה,וּכְסִילִים - הָבִינוּ לֵב!' AND `hqblot` = '<p>החובה ללמד את הפתיים נזכרת כבר בתחילת הספר, \r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2801.htm#4\">משלי א4</a>: \"<q class=\"psuq\">לָתֵת \r\n<strong>לִפְתָאיִם</strong> \r\n<strong>עָרְמָה</strong>, לְנַעַר דַּעַת\r\n וּמְזִמָּה</q>\" (<a href=\"/tnk1/ktuv/mj/01-04.html\">פירוט</a>).</p><p class=\"future\">לב של כסילים נזכר גם ב<a class=\"psuq\" href=\"/tnk1/prqim/t2817.htm#16\">משלי \r\nיז16</a>: \"<q class=\"psuq\">לָמָּה זֶּה מְחִיר בְּיַד \r\n<strong>כְּסִיל</strong> לִקְנוֹת \r\nחָכְמָה, \r\n<strong>וְלֶב</strong> אָיִן?</q>\" (<a href=\"/tnk1/ktuv/mj/17-16.html\">פירוט</a>). </p>' AND 1 LIMIT 1;

