UPDATE `sgulot_hqblot` SET `verse_text` = 'שִׁמְעוּ, כִּי נְגִידִים אֲדַבֵּר, וּמִפְתַּח שְׂפָתַי מֵישָׁרִים;' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 6 AND `verse_text` = 'שִׁמְעוּ, כִּי נְגִידִים אֲדַבֵּר,וּמִפְתַּח שְׂפָתַי מֵישָׁרִים;' AND `hqblot` = '<p class=\"future\">לפי אחד הפירושים, דברי התורה הם \r\n<strong>נגידים</strong>  (מלכים), \"<q class=\"psuq\">כִּי <strong>נְגִידִים</strong> אֲדַבֵּר</q>\", בעוד שהחכמות החיצוניות הן \r\n<strong>שלישים</strong>  (עוזרים), \r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2822.htm#20\">משלי כב20</a>: \"<q class=\"psuq\">הֲלֹא כָתַבְתִּי לְךָ \r\n<strong>שלשום[שָׁלִישִׁים] \r\n</strong> בְּמֹעצוֹת וָדָעַת</q>\" (<a href=\"/tnk1/ktuv/mj/22-20.html\">פירוט</a>).</p>' AND 1 LIMIT 1;

