UPDATE `sgulot_hqblot` SET `verse_text` = 'כִּי אֱמֶת יֶהְגֶּה חִכִּי, וְתוֹעֲבַת שְׂפָתַי רֶשַׁע;' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 7 AND `verse_text` = 'כִּי אֱמֶת יֶהְגֶּה חִכִּי,וְתוֹעֲבַת שְׂפָתַי רֶשַׁע;' AND `hqblot` = '<br />' AND 1 LIMIT 1;

