UPDATE `sgulot_hqblot` SET `verse_text` = 'כֻּלָּם נְכֹחִים לַמֵּבִין, וִישָׁרִים לְמֹצְאֵי דָעַת.' WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 9 AND `verse_text` = 'כֻּלָּם נְכֹחִים לַמֵּבִין,וִישָׁרִים לְמֹצְאֵי דָעַת.' AND `hqblot` = '<p class=\"future\">דברי החכמה הם נכוחים (גלויים) וישרים (ברורים), אולם דברי ה\' לפעמים לא ברורים לשכל אנושי - <a class=\"kll\" title=\"כלל\" href=\"/tnk1/messages/prqim_t2738_1.html\">ה\' מתגלה מן הסערה</a>.</p>' AND 1 LIMIT 1;

