UPDATE `sgulot_hqblot` SET `hqblot` = '<p>א</p>' WHERE `chapter_number` = 1 AND `verse_number` = 0 AND `code` = 'א 0' AND `verse_text` = 'פרק א' AND `tirgum` = '<p>אלה המשלים שאסף שלמה המלך, כדי לדעת חכמה בעצמו וללמד אחרים; שמעו גם אתם והרחיבו את דעתכם!</p><p>הידיעה הראשונה והעיקרית היא יראת ה\';&#160;&#160; גם כשאתם לומדים דברים חדשים, שמרו תמיד על המסורת, התורה והמצוות שקיבלתם מההורים.</p><p>ישנם\r\n חוטאים שינסו לפתות אתכם להצטרף אליהם לפעולות של הונאה ואלימות, \r\nויבטיחו לכם התעשרות קלה ומהירה; אל תלכו איתם! זו מלכודת, למעשה הם \r\nמתכוונים לפגוע בכם ולהתעשר על חשבונכם.</p><p>בניגוד לחוטאים, יש ברחוב גם חכמות, הקוראות לפתיים לחזור וללמוד חכמה. אלה שישובו ללימודים, יזכו למעיין של ידע וחידושים. ומה יקרה לאלה שיתעלמו מקריאת החכמה? </p>' AND `hqblot` = '<br />' AND 1 LIMIT 1;

