UPDATE `sgulot_hqblot` SET `verse_text` = 'וְיֹאכְלוּ מִפְּרִי דַרְכָּם,\r\nוּמִמֹּעֲצֹתֵיהֶם יִשְׂבָּעוּ.', `hqblot` = '<p>ביטוי מקביל ל\"<q class=\"psuq\">ויאכלו מפרי דרכם</q>\" בלשון ימינו הוא \"<q class=\"mfrj\">אכלו את הדייסה שבישלו</q>\"<small class=\"small\"> (ע\"פ ד\"ר נילי סמט)</small>.</p><p class=\"future\">הפועל \r\n<strong>ישבע</strong>   בהקשר של דרכי התנהגות נזכר גם ב<a class=\"psuq\" href=\"/tnk1/prqim/t2814.htm#14\">משלי יד14</a>: \"<q class=\"psuq\">מִדְּרָכָיו יִשְׂבַּע    סוּג לֵב, וּמֵעָלָיו אִישׁ טוֹב</q>\" (<a href=\"/tnk1/ktuv/mj/14-14.html\">פירוט</a>).</p>\r\n' WHERE `chapter_number` = 1 AND `verse_number` = 31 AND `code` = 'א 31' AND `verse_text` = 'וְיֹאכְלוּ מִפְּרִי דַרְכָּם,\nוּמִמֹּעֲצֹתֵיהֶם יִשְׂבָּעוּ.' AND `tirgum` = '<p>- ולכן יוכלו <strong>לאכול </strong>רק<strong> מהפרי </strong>המועט, ההצלחה המעטה שהשיגו כתוצאה מ<strong>דרכם </strong>חסרת האחריות;&#160;&#160;&#160;&#160; ומכיוון שזה  לא יספיק להם, הם ינסו <strong>להשביע</strong> את התיסכול שלהם באכילה <strong>מהעץ</strong> - עצות שלאחר מעשה.</p>' AND `hqblot` = '<p>ביטוי מקביל ל\"<q class=\"psuq\">ויאכלו מפרי דרכם</q>\" בלשון ימינו הוא \"<q class=\"mfrj\">אכלו את הדייסה שבישלו</q>\"<small class=\"small\"> (ע\"פ ד\"ר נילי סמט)</small>.</p><p class=\"future\">הפועל \n<strong>ישבע</strong>   בהקשר של דרכי התנהגות נזכר גם ב<a class=\"psuq\" href=\"/tnk1/prqim/t2814.htm#14\">משלי יד14</a>: \"<q class=\"psuq\">מִדְּרָכָיו יִשְׂבַּע    סוּג לֵב, וּמֵעָלָיו אִישׁ טוֹב</q>\" (<a href=\"/tnk1/ktuv/mj/14-14.html\">פירוט</a>).</p>\n' AND 1 LIMIT 1;

