UPDATE `sgulot_kotrot` SET `kotrt` = 'הציפור שרצתה ארוחת חינם' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 17 AND `verse_text` = 'כִּי חִנָּם מְזֹרָה הָרָשֶׁת\r\nבְּעֵינֵי כָל בַּעַל כָּנָף,' AND `tirgum` = '<p>\r\n- אתה חושב שהם רוצים את טובתך? - <strong>כל בעל כנף </strong>(ציפור), שרואה גרגרים <strong>מזורים</strong> (מפוזרים) על <strong>רשת</strong>, חושבת \"היי, איזה כיף, מישהו עשה לי טובה ופיזר לי אוכל <strong>בחינם</strong>!\" -</p>' AND `ecot` = '<br />' AND `kotrt` = 'כי חנם מזרה הרשת בעיני כל בעל כנף' AND 1 LIMIT 1;

