UPDATE `sgulot_kotrot` SET `kotrt` = 'כשאין פירות - אוכלים עצות' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 31 AND `verse_text` = 'וְיֹאכְלוּ מִפְּרִי דַרְכָּם,\r\nוּמִמֹּעֲצֹתֵיהֶם יִשְׂבָּעוּ.' AND `tirgum` = '<p>- ולכן יוכלו <strong>לאכול </strong>רק<strong> מהפרי </strong>המועט, ההצלחה המעטה שהשיגו כתוצאה מ<strong>דרכם </strong>חסרת האחריות;&#160;&#160;&#160; </p><p>ומכיוון שזה  לא יספיק להם, הם ייאלצו לאכול גם <strong>מהעץ</strong> - עצות שלאחר מעשה - כדי <strong>לשבוע</strong>.</p>' AND `ecot` = '<br />' AND `kotrt` = 'וממועצותיהם ישבעו' AND 1 LIMIT 1;

