UPDATE `sgulot_kotrot` SET `kotrt` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 3 AND `verse_text` = 'לֹא יִכּוֹן אָדָם בְּרֶשַׁע, וְשֹׁרֶשׁ צַדִּיקִים בַּל יִמּוֹט.' AND `tirgum` = '<p>אף <strong>אדם</strong> <strong>לא</strong> יצליח לבנות לעצמו <strong>כַּן</strong> (בסיס יציב) בעזרת מעשי-<strong>רשע</strong>; ולעומת זאת -</p><p>הנוהגים <strong>בצדק</strong> יצליחו ליצור לעצמם <strong>שורש</strong> יציב אשר <strong>לא יתמוטט</strong> לעולם.</p>' AND `ecot` = '<br />' AND `kotrt` = '' AND 1 LIMIT 1;

