UPDATE `sgulot_kotrot` SET `verse_text` = 'בְּאֹרַח צְדָקָה חַיִּים, \r\nוְדֶרֶךְ נְתִיבָה אַל מָוֶת.' WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 28 AND `verse_text` = 'בְּאֹרַח צְדָקָה חַיִּים, וְדֶרֶךְ נְתִיבָה אַל מָוֶת.' AND `tirgum` = '<p>כשהולכים <strong>באורח</strong> (דרך) של <strong>צדקה</strong>, נוהגים בצדק והגינות, מוצאים <strong>חיים</strong>;</p><p>וכשהולכים רק <strong>דרך</strong> ה<strong>נתיב</strong> של הצדקה, מקפידים לנהוג רק בצדק והגינות, ניצלים מ<strong>מוות</strong>.</p>' AND `ecot` = '<br />' AND `kotrt` = 'באורח צדקה חיים, ודרך נתיבה אלמוות' AND 1 LIMIT 1;

