UPDATE `sgulot_kotrot` SET `verse_text` = 'טוֹב פַּת חֲרֵבָה וְשַׁלְוָה בָהּ, \r\nמִבַּיִת מָלֵא זִבְחֵי רִיב.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 1 AND `verse_text` = 'טוֹב פַּת חֲרֵבָה וְשַׁלְוָה בָהּ, מִבַּיִת מָלֵא זִבְחֵי רִיב.' AND `tirgum` = '<p>\r\n<strong>טוב</strong>    יותר להקריב לה\' \r\n<strong>פת</strong>    לחם פשוטה \r\n<strong>וחרבה</strong>    (יבשה) \r\n<strong>בשלוה</strong>    ושלום, \r\n<strong>מלמלא</strong>    את \r\n<strong>בית</strong>   המקדש  \r\n<strong>בזבחי</strong>    שלמים יקרים תוך כדי \r\n<strong>מריבות</strong>    בין אדם לחברו.</p>' AND `ecot` = '<br />' AND `kotrt` = 'הקשר בין קרבנות לבין שלוה ומריבה' AND 1 LIMIT 1;

