UPDATE `sgulot_kotrot` SET `verse_text` = 'מַצְרֵף לַכֶּסֶף, \r\nוְכוּר לַזָּהָב, \r\nוּבֹחֵן לִבּוֹת ה\'.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 3 AND `verse_text` = 'מַצְרֵף לַכֶּסֶף, וְכוּר לַזָּהָב, וּבֹחֵן לִבּוֹת ה\'.' AND `tirgum` = '<p>\r\n<strong>מצרף</strong>     (תנור זיקוק) משמש להפרדת \r\n<strong>כסף</strong>   מסיגי הפסולת הדבוקים אליו; &#160; \r\n<strong>כור</strong>     (תנור היתוך) משמש להפרדת \r\n<strong>זהב</strong>     מפסולת;&#160;&#160; כך גם \r\n<strong>ה\'</strong>, מעמיד את בני האדם במבחנים לוהטים כתנור,&#160;\r\n<strong>בוחן</strong>     את \r\n<strong>ליבותיהם</strong>   ומפריד בין האנשים שליבם טהור לבין הצבועים.\r\n</p>' AND `ecot` = '<p>מדוע בחינת הלבבות של ה\' נמשלת ל<strong>מצרף לכסף וכור לזהב</strong>?</p><p>- אולי כדי ללמדנו, שהדגש בבחינה הוא על התכונות הטובות: \"<q class=\"mfrj\">ראוי שהבחינה העצמית תתחיל בחיפוש אחרי הנקודות החיוביות שבאישיותו, ולא במה ששלילי אצלו, כדרכם של מחפשי הזהב, שעיניהם תלויות לזהב ולא לפסולת. כך יעלה בידו לזכך את דרכיו בשמחה</q>\"<small class=\"small\"> (הרב אורי שרקי, \"קרוב אליך\" 1, חשון ה\'תשע\"ד)</small>.</p>' AND `kotrt` = 'בוחן ליבות ה\'' AND 1 LIMIT 1;

