UPDATE `sgulot_kotrot` SET `verse_text` = 'לֹא נָאוָה לְנָבָל שְׂפַת יֶתֶר, \r\nאַף כִּי לְנָדִיב שְׂפַת שָׁקֶר.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 7 AND `verse_text` = 'לֹא נָאוָה לְנָבָל שְׂפַת יֶתֶר, אַף כִּי לְנָדִיב שְׂפַת שָׁקֶר.' AND `tirgum` = '<p>\r\n<strong>לא נאה</strong>    אפילו לאדם \r\n<strong>נבל</strong>, גס והמוני, להבטיח \r\n<strong>בשפתיו יותר</strong>    ממה שיעשה בפועל;&#160;&#160; <strong>אף כי</strong>    (קל וחומר), אין זה נאה לאדם \r\n<strong>נדיב</strong>, נעלה ונכבד, להבטיח \r\n<strong>בשפתיו</strong>    דברי \r\n<strong>שקר</strong>    שלא יעשה כלל.</p><p>/ <strong>לא רצוי</strong> להגיד על אדם <strong>נבל</strong> שיש לו <strong>יתרונות</strong> ומעלות שאין בו;&#160;&#160; <strong>אף כי</strong> (קל וחומר) אין זה רצוי להגיד על אדם <strong>נדיב</strong> דברי גנאי שהם <strong>שקר</strong>.</p>' AND `ecot` = '<br />' AND `kotrt` = 'מה יכול הנדיב ללמוד מן הקמצן' AND 1 LIMIT 1;

