UPDATE `sgulot_kotrot` SET `verse_text` = 'בְּכָל עֵת אֹהֵב הָרֵעַ, \r\nוְאָח לְצָרָה יִוָּלֵד.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 17 AND `verse_text` = 'בְּכָל עֵת אֹהֵב הָרֵעַ, וְאָח לְצָרָה יִוָּלֵד.' AND `tirgum` = '<p>\r\n<strong>בכל עת</strong>   כדאי לך לגלות \r\n<strong>אהבה</strong>   כלפי \r\n<strong>הרֵע</strong>   (השכן, המַכָּר); &#160; כך, כשתבוא עליך \r\n<strong>צרה</strong>, תגלה \r\n<strong>שנולד</strong>   לך \r\n<strong>אח</strong>   חדש - אותו רֵע יבוא לעזרתך כאילו הוא אחיך.</p>' AND `ecot` = '<br />' AND `kotrt` = 'בכל עת אוהב הרע, ואח לצרה ייוולד' AND 1 LIMIT 1;

