UPDATE `sgulot_kotrot` SET `verse_text` = 'אֹהֵב פֶּשַׁע, אֹהֵב מַצָּה, \r\nמַגְבִּיהַּ פִּתְחוֹ - מְבַקֶּשׁ שָׁבֶר.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 19 AND `verse_text` = 'אֹהֵב פֶּשַׁע, אֹהֵב מַצָּה, מַגְבִּיהַּ פִּתְחוֹ - מְבַקֶּשׁ שָׁבֶר.' AND `tirgum` = '<p>מי \r\n<strong>שאוהב</strong>  למצוא \r\n<strong>פשעים</strong>  ופגמים אצל הזולת, \r\n<strong>אוהב מצה</strong>  מריבה ומחלוקת עם הזולת, ו<strong>מגביה</strong>  את \r\n<strong>פתח</strong>  הכניסה לשכונתו כך שרק אנשים \"ברמה מספיק גבוהה\" יוכלו להיכנס - כל אחד מאלה \r\n<strong>מבקש</strong>  \r\n<strong>שבר</strong>  (קרע ופילוג) בעם, וגורם \r\n<strong>שבר</strong>  (צרה ואסון) לעצמו.</p>' AND `ecot` = '<p>\r\nישנם זרמים מסויימים בעם ישראל, ש\"תנאי הכניסה\" לתוכם הם גבוהים מאד. בחור \r\nמ\"בחוץ\" לעולם לא יוכל להתחתן עם בחורה מ\"בפנים\", והורים מ\"בחוץ\" לעולם לא \r\nיוכלו לשלוח את ילדיהם לבתי ספר מ\"בפנים\", ועוד. אותם זרמים טוענים, שהמטרה\r\n שלהם היא להגן על עצמם ועל ילדיהם מפני פשעים שנמצאים \"בחוץ\". על-פי ספר \r\nמשלי, תנאי כניסה גבוהים באופן מוגזם עלולים לגרום לשֶבֶר בחברה:\r\n</p>  \r\n  \r\n<p><strong>מַצָּה</strong>   \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/mca.html\">= מריבה</a>;&#160;&#160;&#160;\r\n<strong>אוהב פשע אוהב מצה</strong>      = מי שאוהב לראות פשעים, פגמים ומעשים רעים \r\nאצל אחרים, כאילו אוהב לריב איתם, כי חיפוש פשעים אצל הזולת הוא אחת הסיבות\r\n העיקריות למריבה: \"<q class=\"mfrj\">מי \r\nשחביב  עליו\r\nלמצוא \r\nפשע, תמיד ימצא איזה \r\n  \r\nפשע. הסוטה יגלה בכל מקום ערווה</q>\" \r\n<small>(<a href=\"http://mazalart.blogspot.com/\">חנן מזל</a>, אתר הניווט בתנ\"ך ה\'תשס\"ט)</small>. \r\n  \r\n \r\n</p><p>\r\n<strong>פתחו</strong>         = הכניסה לביתו, למשפחתו, לבית הספר שלו או לקהילה שהוא משתייך אליה;&#160;&#160; \r\n<strong>מגביה פתחו</strong>        = הקובע \"תנאי קבלה\" גבוהים, \"<q class=\"mfrj\">כאילו רק חברה \"גבוהה\" עוברת סף זה</q>\" \r\n<small>(רמי ניר, אתר הניווט בתנ\"ך ה\'תשנ\"ז)</small>. \r\n</p>   \r\n  \r\n  \r\n<p>\r\n<strong>מבקש \r\n</strong>        \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/bqj.html\">= רוצה</a>;&#160;&#160; \r\n<strong>שֶבֶר</strong>         = קרע ופירוד בין חלקי העם, וגם צרה ואסון; &#160; \r\n<strong>מגביה פתחו מבקש שבר</strong>      = מי שמגביה את פתח הכניסה לקהילתו, וקובע\r\n תנאי קבלה שמשאירים את רוב הציבור בחוץ, כאילו רוצה שיהיה שבר בעם ישראל, \r\nובסופו של דבר עלול להביא אסון על עצמו ועל קהילתו: \"<q class=\"mfrj\">בהקפדה על \"טוהר מחננו\"\r\nיצרנו שבר ושינאה</q>\" \r\n<small>(חנן מזל, שם)</small>.</p>' AND `kotrt` = 'אוהב פשע אוהב מצה, מגביה פתחו מבקש שבר' AND 1 LIMIT 1;

