UPDATE `sgulot_kotrot` SET `verse_text` = 'עִקֶּשׁ לֵב לֹא יִמְצָא טוֹב, \r\nוְנֶהְפָּךְ בִּלְשׁוֹנוֹ יִפּוֹל בְּרָעָה.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 20 AND `verse_text` = 'עִקֶּשׁ לֵב לֹא יִמְצָא טוֹב, וְנֶהְפָּךְ בִּלְשׁוֹנוֹ יִפּוֹל בְּרָעָה.' AND `tirgum` = '<p>אדם \r\n<strong>עיקש</strong>, עקום וקשה \r\n<strong>בליבו</strong>  ודעותיו, \r\n<strong>לא</strong>  \r\n<strong>ימצא</strong>,לא יבחין \r\n<strong>בטוב</strong>  שיש באנשים וברעיונות חדשים, ולכן \r\n<strong>לא ימצא</strong>, לא ישיג תוצאות \r\n<strong>טובות</strong>  בקשרים חברתיים בלימודים ובעסקים;&#160;&#160; אבל הקיצוניות השניה מסוכנת יותר -</p> \r\n<p>אדם \r\n<strong>הפכפך</strong>, לא יציב ולא עקבי \r\n<strong>בלשונו</strong>  ודבריו, עלול \r\n<strong>ליפול</strong>  ולטעות בשגיאה \r\n<strong>רעה</strong>.</p>' AND `ecot` = '<br />' AND `kotrt` = 'עיקש לב לא ימצא טוב, ונהפך בלשונו ייפול ברעה' AND 1 LIMIT 1;

