UPDATE `sgulot_kotrot` SET `verse_text` = 'יֹלֵד כְּסִיל לְתוּגָה לוֹ, \r\nוְלֹא יִשְׂמַח אֲבִי נָבָל.' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 21 AND `verse_text` = 'יֹלֵד כְּסִיל לְתוּגָה לוֹ,\r\nוְלֹא יִשְׂמַח אֲבִי נָבָל.' AND `tirgum` = '<p>אב \r\n<strong>היולד</strong>  (יוצר) בן \r\n<strong>כסיל</strong>  - \r\n<strong>תוגה</strong>   (צער עמוק) יהיה \r\n<strong>לו</strong>;&#160;&#160; וגם \r\n<strong>אביו</strong>   של בן \r\n<strong>נבל</strong>   (חסר דרך-ארץ) \r\n<strong>לא ישמח</strong>.</p>' AND `ecot` = '<br />' AND `kotrt` = 'יולד כסיל לתוגה לו, ולא ישמח אבי נבל' AND 1 LIMIT 1;

