UPDATE `sgulot_kotrot` SET `verse_text` = 'שָׁמַיִם - לָרוּם,וָאָרֶץ - לָעֹמֶק; \r\nוְלֵב מְלָכִים - אֵין חֵקֶר.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 3 AND `verse_text` = 'שָׁמַיִם - לָרוּם,וָאָרֶץ - לָעֹמֶק;וְלֵב מְלָכִים - אֵין חֵקֶר.' AND `tirgum` = '<p>- ועוד: כמו \r\n<strong>שהשמיים</strong>   הם \r\n<strong>רמים</strong>   (גבוהים), \r\n<strong>והארץ עמוקה</strong>, כך \r\n<strong>לב מלכים</strong>   מלא בטירדות רבות עד \r\n<strong>אין חקר</strong>, ולכן כשפונים אליהם בבקשה יש \r\n<strong>לחקור</strong>   ולהציג את הבקשה בפירוט.</p> \r\n<p>/ כמו שקשה לראות את קצה \r\n<strong>הרום</strong>  (הגובה) של \r\n<strong>השמיים</strong>, וכמו שקשה לראות את קצה \r\n<strong>העומק</strong>  של \r\n<strong>הארץ</strong>, כך קשה מאד&#160;\r\n<strong>לחקור</strong>  ולהבין עד הסוף את כל החכמה הדרושה \r\n<strong>למלכים</strong>  על-מנת להנהיג את המדינה; חכמת המלוכה היא עמוקה וקשה להשגה.</p>' AND `ecot` = '<p>\r\nחשוב למתוח ביקורת על השלטון, אבל חשוב גם שהביקורת תהיה מציאותית:</p>  \r\n<p>\r\n<strong>לב מלכים</strong>      = המחשבות שבליבם של השליטים; &#160; \r\n<strong>אין חקר</strong>      \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/xqr.html\">= הן כל-כך מרובות עד שאי אפשר להגיע עד תכליתן</a>; &#160;&#160;\r\n</p>    \r\n<p>כמו \r\n<strong>שהשמים</strong>      גבוהים מאד, ואדם פשוט אינו יכול להגיע עד קציהם; וכמו \r\n<strong>שהארץ</strong>      עמוקה מאד, ואדם פשוט אינו יכול להגיע עד תחתיתה; כך \r\nהמחשבות הצריכות להיות בליבו של המנהיג, על מנת שיוכל להנהיג את המדינה, הן\r\n רבות ומורכבות כל-כך, עד שאדם פשוט אינו יכול להבין את כולן עד הסוף. לא \r\nכל פעולה שנראית לנו טובה היא אכן טובה, ולא כל פעולה שנראית רעה היא אכן \r\nרעה; יש הרבה שיקולים שאנחנו לא מודעים להם, ולכן יש למתוח ביקורת מתוך \r\nענוה.\r\n</p>' AND `kotrt` = 'לא קל לנהל מדינה' AND 1 LIMIT 1;

