UPDATE `sgulot_kotrot` SET `verse_text` = 'הָגוֹ סִיגִים מִכָּסֶף, \r\nוַיֵּצֵא לַצֹּרֵף כֶּלִי =' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 4 AND `verse_text` = 'הָגוֹ סִיגִים מִכָּסֶף, וַיֵּצֵא לַצֹּרֵף כֶּלִי =' AND `tirgum` = '<p>\r\n<strong>הגו</strong>     (הסר, סלק) \r\n<strong>את הסיגים</strong>     (הפסולת) \r\n<strong>מהכסף</strong>; &#160; רק אחר-כך \r\n<strong>ייצא לצורף</strong>     \r\n<strong>כלי</strong>     יפה מכסף טהור. כך גם -</p>' AND `ecot` = '<br />' AND `kotrt` = 'טיהור המערכת הפוליטית - שלב ראשון בביסוס השלטון' AND 1 LIMIT 1;

