UPDATE `sgulot_kotrot` SET `verse_text` = '= הָגוֹ רָשָׁע לִפְנֵי מֶלֶךְ, \r\nוְיִכּוֹן בַּצֶּדֶק כִּסְאוֹ.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 5 AND `verse_text` = '= הָגוֹ רָשָׁע לִפְנֵי מֶלֶךְ, וְיִכּוֹן בַּצֶּדֶק כִּסְאוֹ.' AND `tirgum` = '<p>- \r\n<strong>הגו</strong>     (הסר, פטר) כל פקיד \r\n<strong>רשע מלפני</strong>    \r\n<strong> המלך</strong>     (מהשירות הציבורי); &#160; רק אחר-כך \r\n<strong>ייכון</strong>     (יתבסס) \r\n<strong>בצדק כסא</strong>     מלכותו ושלטונו.</p>' AND `ecot` = '<br />' AND `kotrt` = 'ביאור:משלי כה5' AND 1 LIMIT 1;

