UPDATE `sgulot_kotrot` SET `verse_text` = '- כִּי טוֹב אֲמָר לְךָ \'עֲלֵה הֵנָּה!\',\r\nמֵהַשְׁפִּילְךָ לִפְנֵי נָדִיב אֲשֶׁר רָאוּ עֵינֶיךָ.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 7 AND `verse_text` = '- כִּי טוֹב אֲמָר לְךָ \'עֲלֵה הֵנָּה!\',מֵהַשְׁפִּילְךָ לִפְנֵי נָדִיב אֲשֶׁר רָאוּ עֵינֶיךָ.' AND `tirgum` = '<p>- \r\n<strong>כי טוב</strong>    יותר שתעמוד במקום נמוך מהראוי לך, עד שהמלך בעצמו יזהה שאתה מתאים לתפקיד, \r\n<strong>ויאמר לך</strong>    \"<strong>עלה הנה!</strong>\" (קבל את התפקיד), משיקרה לך ההיפך - תעמוד במקום גבוה מהראוי לך, והמלך יצווה עליך לרדת ו<strong>ישפיל</strong>    אותך \r\n<strong>לפני</strong>    ה<strong>נדיב</strong>    (האציל) המתחרה שלך.</p>' AND `ecot` = '<br />' AND `kotrt` = 'איך לבחור מקום בחיים' AND 1 LIMIT 1;

