UPDATE `sgulot_kotrot` SET `verse_text` = 'רִיבְךָ רִיב אֶת רֵעֶךָ, \r\nוְסוֹד אַחֵר אַל תְּגָל;' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 9 AND `verse_text` = 'רִיבְךָ רִיב אֶת רֵעֶךָ,וְסוֹד אַחֵר אַל תְּגָל;' AND `tirgum` = '<p>- אם יש לך סיבה \r\n<strong>לריב</strong>, \r\n<strong>ריב</strong>    רק \r\n<strong>עם רעך</strong>, בינו לבינך, \r\n<strong>ואל תגלה</strong>    את \r\n<strong>הסוד</strong>    על המריבה לאדם \r\n<strong>אחר</strong>    -</p>' AND `ecot` = '<br />' AND `kotrt` = 'אל תצא לריב מהר' AND 1 LIMIT 1;

