UPDATE `sgulot_kotrot` SET `verse_text` = 'פֶּן יְחַסֶּדְךָ שֹׁמֵעַ, \r\nוְדִבָּתְךָ לֹא תָשׁוּב.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 10 AND `verse_text` = 'פֶּן יְחַסֶּדְךָ שֹׁמֵעַ,וְדִבָּתְךָ לֹא תָשׁוּב.' AND `tirgum` = '<p>- \r\n<strong>פן השומע</strong>, שישמע את הדברים הרעים שאתה אומר על יריבך, \r\n<strong>יחסד</strong>    (יבזה) דווקא \r\n<strong>אותך</strong>    ולא את יריבך;&#160;&#160; ו<strong>הדיבה</strong>    (הגנות), שדבקה בך על-כך שאתה ממהר לריב לחינם, \r\n<strong>לא תשוב</strong>    אחור - לא תוכל להחזיר את הגלגל לאחור ולטהר את שמך.</p>' AND `ecot` = '<br />' AND `kotrt` = 'ביאור:משלי כה10' AND 1 LIMIT 1;

